// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

/**
 * @title HelixBase contract
 * @notice This is our Base contract that most other contracts inherit from. It includes many standard
 *  useful abilities like upgradeability, pausability, access control, and re-entrancy guards.
 * @author Helix
 */

contract DChainBase is
    AccessControlUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable
{
    bytes32 public constant SUB_ADMIN_ROLE = keccak256("SUB_ADMIN_ROLE");
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    // Pre-reserving a few slots in the base contract in case we need to add things in the future.
    // This does not actually take up gas cost or storage cost, but it does reserve the storage slots.
    // See OpenZeppelin's use of this pattern here:
    // https://github.com/OpenZeppelin/openzeppelin-contracts-ethereum-package/blob/master/contracts/GSN/Context.sol#L37
    uint256[50] private __gap1;
    uint256[50] private __gap2;
    uint256[50] private __gap3;
    uint256[50] private __gap4;

    // solhint-disable-next-line func-name-mixedcase
    function __DChainBase_init(address owner) public onlyInitializing {
        require(owner != address(0), "Owner cannot be the zero address");
        __AccessControl_init();
        __Pausable_init();
        __ReentrancyGuard_init();

        _setupRole(OWNER_ROLE, owner);
        _setupRole(PAUSER_ROLE, owner);

        _setRoleAdmin(SUB_ADMIN_ROLE, OWNER_ROLE);
        _setRoleAdmin(PAUSER_ROLE, OWNER_ROLE);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function isAdmin() public view returns (bool) {
        return hasRole(OWNER_ROLE, _msgSender());
    }

    modifier onlyAdmin() {
        require(isAdmin(), "Must have admin role to perform this action");
        _;
    }
}

contracts/DDXStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IOracleSimple.sol";
import "./interfaces/IDChainStaking.sol";
import "./interfaces/IDDXVault.sol";
import "./interfaces/IDDXStaking.sol";
import "./DChainBase.sol";

contract DDXStaking is DChainBase, IDDXStaking {
    struct StakingInfo {
        uint256 totalStakesInUSD;
        uint256 claimedInterest;
        uint64 dueDate;
        uint64 timeStake;
        uint64 lastClaimedTime;
        uint64 lastUpdatedTime;
        uint64 createdAt;
    }

    // Info of each pool.
    struct PoolInfo {
        uint256 minDepositInUSD; // minimun amount that can be deposit
        uint64 period; // period of pool
        uint64 APR; // APR of pool : 5000 = 50%
        bool status; // status of pool
    }

    struct OfferedCurrency {
        uint256 decimal;
        uint256 rate;
    }

    uint64 private constant INTEREST_RATE_PRECISION_POINT = (10 ** 5); 
    uint32 private constant ONE_DAY_IN_SECONDS = 1 days;
    uint32 private constant ONE_YEAR_IN_SECONDS = 365 days;

    IERC20 public rewardToken;
    IDDXVault public vault;

    address public treasury;
    address public admin;

    address public oracle;

    uint256 public totalStaked;

    bool public emergencyCancelled;

    // Asset token -> Offered Currency (to $)
    mapping(address => OfferedCurrency) public offeredCurrencies;
    // user -> current staking infos
    mapping(uint => mapping(address => StakingInfo)) public stakingInfos;

    // Info of each pool.
    PoolInfo[] public poolInfo;

    event ContractCreated(uint256 indexed contractId, address indexed user, address indexed stakeToken, address referrer, uint256 amount, uint256 amountInUSD);
    event StakingContractCreated(address indexed user, address indexed referrer, uint indexed contractId, uint stakingAmount);
    event Withdraw(address indexed user, uint256 amount);
    event RewardHarvested(address indexed claimer, uint indexed pid, uint256 amount, uint256 amountInUSD);
    event Withdrawed(address indexed claimer, uint indexed pid, uint256 amount, uint256 amountInUSD);
    event PoolAdded(uint256 _minDepositInUSD, uint64 _APR, uint128 _period);    

    function initialize(address _owner, address _oracle, address _treasury, IERC20 _rewardToken) external initializer {
        __DChainBase_init(_owner);

        /// @dev: ZA - Zero address
        require(_treasury != address(0), "ZA");
        require(address(_rewardToken) != address(0), "ZA");

        treasury = _treasury;
        rewardToken = _rewardToken;
        admin = msg.sender;
        oracle = _oracle;

        _setupRole(SUB_ADMIN_ROLE, _owner);

        // Max approve for transfer from 
        rewardToken.approve(address(this), type(uint256).max);
    }

    /// -----------------------------------
    /// --------- Update Function ---------
    /// -----------------------------------

    function setDDXVault(IDDXVault _vault) external onlyRole(SUB_ADMIN_ROLE) {
        require(address(_vault) != address(0), "pool: DDX vault cannot be zero address");
        vault = _vault;
    }


    // how to convert from 1 Token - to $
    function setOfferedCurrency(address _currency, uint _rate, uint _decimal) external onlyRole(SUB_ADMIN_ROLE) {
        OfferedCurrency storage offeredCurrency = offeredCurrencies[_currency];
        offeredCurrency.rate = _rate;
        offeredCurrency.decimal = _decimal;
    }


    /// -----------------------------------
    /// ---------- Core Function ----------
    /// -----------------------------------

    function addPool(uint256 _minDepositInUSD, uint64 _APR, uint64 _period) external onlyRole(SUB_ADMIN_ROLE) {        
        poolInfo.push(PoolInfo({
            minDepositInUSD: _minDepositInUSD,
            APR: _APR,
            period: _period,
            status: true
        }));

        emit PoolAdded(_minDepositInUSD, _APR, _period);
    }

    function depositByVault(uint _pid, uint _originAmount, uint _lockedAmount, address _user) external override whenNotPaused nonReentrant {
        require(_msgSender() == address(vault), "pool: Not stake by vault");
        require(IDDXVault(vault).startVestingTime() <= block.timestamp, "pool: not allow to use this after vesting time started");

        uint totalStakeAmount = _originAmount + _lockedAmount;

        require(totalStakeAmount > 0, "pool: amount cannot be zero");

        address sender = _user;

        // Forward from vault to smart contract 
        _forwardRewardToken(address(vault), _originAmount);
        uint amountOutInUSD = _validateMinimumStakingAmount(_pid, totalStakeAmount);

        _updateUserStakingInfo(_pid, sender, totalStakeAmount, amountOutInUSD);
    }

    function deposit(uint256 _pid, uint256 _amount) external whenNotPaused nonReentrant {
        address sender = msg.sender;

        PoolInfo storage pool = poolInfo[_pid];
        StakingInfo storage stakingInfo = stakingInfos[_pid][msg.sender];

        require(_amount > 0, "pool: amount cannot be zero");
        require(pool.status, "This pool is close");

        // Forward user tokens to smart contract 
        uint stakingAmount = _forwardRewardToken(sender, _amount);
        uint amountOutInUSD = _validateMinimumStakingAmount(_pid, stakingAmount);

        if (stakingInfo.timeStake == 0) {
            stakingInfo.createdAt = uint64(block.timestamp);
            stakingInfo.timeStake = uint64(block.timestamp);
            stakingInfo.dueDate = stakingInfo.timeStake +  pool.period;
        }

        stakingInfo.lastClaimedTime = uint64(block.timestamp);
        stakingInfo.lastUpdatedTime = uint64(block.timestamp);
        stakingInfo.totalStakesInUSD += amountOutInUSD;

        totalStaked += stakingAmount;

        _updateUserStakingInfo(_pid, sender, stakingAmount, amountOutInUSD);
    }

    function withdraw(uint _pid) external whenNotPaused nonReentrant {
        address sender = msg.sender;

        StakingInfo storage stakingInfo = stakingInfos[_pid][sender];
        require(stakingInfo.totalStakesInUSD > 0, "pool: total stakes must greater than 0");
        require(block.timestamp > stakingInfo.dueDate, "pool: due date req not meet");

        _harvest(_pid, sender);

        uint256 rewardsInRewardTokens = _convertUSDToRewardToken(stakingInfo.totalStakesInUSD);

        require(rewardsInRewardTokens > 0, "pool: reward not enough to harvest");

        // Transfer the interest amount to owner
        rewardToken.transferFrom(treasury, sender, rewardsInRewardTokens);

        delete stakingInfos[_pid][sender];

        emit Withdrawed(sender, _pid, rewardsInRewardTokens, stakingInfo.totalStakesInUSD);
    }

    function claimReward(uint _pid) external nonReentrant whenNotPaused {
        address sender = msg.sender;
        _harvest(_pid, sender);
    }

    function pendingRewardInUSD(uint256 _pid, address _user) public view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        StakingInfo storage stakingInfo = stakingInfos[_pid][_user];

        uint64 current = uint64(block.timestamp);

        if (current <= stakingInfo.lastClaimedTime) {
            return 0;
        }

        if (current > stakingInfo.dueDate) {
            current = stakingInfo.dueDate;
        }

        // Get total number of interest periods from the last time claimed
        uint64 passedDuration = uint64(block.timestamp) - stakingInfo.lastClaimedTime; 

        uint256 pendingInterest = stakingInfo.totalStakesInUSD * passedDuration * pool.APR / INTEREST_RATE_PRECISION_POINT / ONE_YEAR_IN_SECONDS;

        return pendingInterest;
    }

    function getAmountDDXByUSD(uint _amountInUSD) external override returns(uint) {
        return _convertUSDToRewardToken(_amountInUSD);
    }

    // Reward token - asset
    function _validateMinimumStakingAmount(uint256 _pid, uint256 _stakingAmount) internal returns(uint256 totalStakingAmountInUSD) {
        PoolInfo storage pool = poolInfo[_pid];
        StakingInfo storage stakingInfo = stakingInfos[_pid][msg.sender];

        uint256 amountOut;

        if (oracle != address(0)) {
            // Update oracle Pricing when time elapsed has passed
            if (block.timestamp - IOracleSimple(oracle).getBlockTimestampLast() >  IOracleSimple(oracle).PERIOD()) { 
                IOracleSimple(oracle).update();
            }

            amountOut = IOracleSimple(oracle).consult(address(rewardToken), _stakingAmount);
        }

        if (amountOut > 0) {
            totalStakingAmountInUSD = amountOut;
        } else { 
            // This case fallback to pre setup price
            totalStakingAmountInUSD = getStakingAmountInUSD(address(rewardToken), _stakingAmount);  // Get amount in case we don't have an active oracle
        }

        stakingInfo.totalStakesInUSD += totalStakingAmountInUSD;

        // Needs to be make sure staking amount by $ greater than minimum amount
        require(stakingInfo.totalStakesInUSD >= pool.minDepositInUSD, "pool: minimum staking amount not reached");
    }

    function _convertUSDToRewardToken(uint256 _amountInUSD) internal returns(uint256) {
        if (oracle != address(0)) {
            // Update oracle Pricing when time elapsed has passed
            if (block.timestamp - IOracleSimple(oracle).getBlockTimestampLast() >  IOracleSimple(oracle).PERIOD()) { 
                IOracleSimple(oracle).update();
            }

            return IOracleSimple(oracle).consult(IOracleSimple(oracle).token1(), _amountInUSD);
        }

        // Get amount in case we don't have an active oracle
        return getStakingAmountByPegToken(address(rewardToken), _amountInUSD); 
    }

    /**
     * @dev Get Staking token amount in offered currencies
     * @param _amount Amount of purchase token
     * @return Return amount of respective IDO token
     */
    function getStakingAmountInUSD(
        address _token,
        uint _amount
    ) public view returns (uint) {
        OfferedCurrency memory currency = offeredCurrencies[_token];
        return
            (_amount * (10 ** currency.decimal)) / currency.rate;
    }

    function getStakingAmountByPegToken(
        address _token,
        uint _amount
    ) public view returns (uint) {
        OfferedCurrency memory currency = offeredCurrencies[_token];
        return
            (_amount * currency.rate) / (10 ** currency.decimal);
    }

    function _forwardRewardToken(address sender, uint256 amount) internal returns(uint256) {
        uint256 totalStakingbefore = rewardToken.balanceOf(address(this));
        // Locking principal deposit amount
        rewardToken.transferFrom(sender, address(this), amount);
        // Get balance of contract after staking
        uint256 totalStakingAfter = rewardToken.balanceOf(address(this));

        return totalStakingAfter - totalStakingbefore;
    }

    function _updateUserStakingInfo(uint _pid, address _user, uint _stakingAmount, uint _amountOutInUSD) internal {
        PoolInfo storage pool = poolInfo[_pid];
        StakingInfo storage stakingInfo = stakingInfos[_pid][_user];

        if (stakingInfo.timeStake == 0) {
            stakingInfo.createdAt = uint64(block.timestamp);
            stakingInfo.timeStake = uint64(block.timestamp);
            stakingInfo.dueDate = stakingInfo.timeStake +  pool.period;
        }

        stakingInfo.lastClaimedTime = uint64(block.timestamp);
        stakingInfo.lastUpdatedTime = uint64(block.timestamp);
        stakingInfo.totalStakesInUSD += _amountOutInUSD;

        totalStaked += _stakingAmount;
    }

    function _harvest(uint _pid, address _sender) internal {
        StakingInfo storage stakingInfo = stakingInfos[_pid][_sender];

        uint256 rewardsInUSD = pendingRewardInUSD(_pid, _sender);
        uint256 rewardsInRewardTokens = _convertUSDToRewardToken(rewardsInUSD);

        require(rewardsInRewardTokens > 0, "pool: reward not enough to harvest");

        // Transfer the interest amount to owner
        rewardToken.transferFrom(treasury, _sender, rewardsInRewardTokens);

        uint64 lastClaimedTime = uint64(block.timestamp);

        if (lastClaimedTime > stakingInfo.dueDate) {
            lastClaimedTime = stakingInfo.dueDate;
        }

        stakingInfo.lastUpdatedTime = uint64(block.timestamp);
        stakingInfo.lastClaimedTime = lastClaimedTime;
        stakingInfo.claimedInterest += rewardsInUSD;

        emit RewardHarvested(_sender, _pid, rewardsInRewardTokens, rewardsInUSD);
    }

    /// -----------------------------------
    /// --------- Pause Function ----------
    /// -----------------------------------

    function pause() external onlyAdmin {
        _pause();
    }

    function unpause() external onlyAdmin {
        _unpause();
    }
}

contracts/DDXVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./DChainBase.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IDChainStaking.sol";
import "./interfaces/IDDXVault.sol";
import "./libraries/TransferHelper.sol";

contract DDXVault is DChainBase, IDDXVault {
    struct VestingSchedule {
        // beneficiary of tokens after they are released
        address beneficiary;
        // cliff time of the vesting start in seconds since the UNIX epoch
        uint256 cliff;
        // start time of the vesting period in seconds since the UNIX epoch
        uint256 start;
        // duration of the vesting period in seconds
        uint256 duration;
        // duration of a slice period for the vesting in seconds
        uint256 slicePeriodSeconds;
        // whether or not the vesting is revocable
        bool revocable;
        // total amount of tokens to be released at the end of the vesting
        uint256 amountTotal;
        // amount of tokens released
        uint256 released;
        // whether or not the vesting has been revoked
        bool revoked;
    }

    uint256 private constant PRECISION_POINT = 10000;

    uint public override startVestingTime;
    uint public vestingDuration;
    uint public vestingPeriodInSeconds;

    address public treasury;
    address public admin;
    address public DDXStaking;
    address public DWStaking;

    IERC20 public rewardToken;

    bool public override rewardApplicable;

    uint256 private vestingSchedulesTotalAmount;

    bytes32[] private vestingSchedulesIds;
    mapping(bytes32 => VestingSchedule) private vestingSchedules;
    mapping(address => uint256) private holdersVestingCount;

    function initialize(
        address _owner,
        IERC20 _rewardToken, 
        address _treasury, 
        address _DWStaking,
        address _DDXStaking,
        uint _startVestingTime
    ) external initializer {
        /// @dev: ZA - Zero address
        require(_DWStaking != address(0), "ZA");
        require(_DDXStaking != address(0), "ZA");
        require(address(_rewardToken) != address(0), "ZA");
        require(_treasury != address(0), "ZA");

        startVestingTime = _startVestingTime;

        rewardToken = _rewardToken;
        treasury = _treasury;
        DDXStaking = _DDXStaking;
        DWStaking = _DWStaking;

        vestingDuration = 20 * 30 days; // 20 monthds
        vestingPeriodInSeconds = 30 days;
        rewardApplicable = true;

        _setupRole(SUB_ADMIN_ROLE, _owner);
    }

    function stakeWithVault(
        uint256 _originAmount,
        uint256 _lockedAmount
    ) external {
        require(block.timestamp <= startVestingTime, "pool: not allow to stake after vesting start");

        address sender = msg.sender;
        VestingSchedule memory vestingSchedule = getVestingSchedule(computeVestingScheduleIdForAddressAndIndex(sender, 0));

        require(vestingSchedule.beneficiary == sender, "pool: vesting schedule not existed");
        require(rewardToken.allowance(sender, address(this)) >= _originAmount, "pool: allowance not enough");
        require(vestingSchedule.amountTotal >= _lockedAmount, "pool: vesting schedule amount total not enough to cover staking");

        rewardToken.approve(DWStaking, _originAmount);

        vestingSchedule.amountTotal -= _lockedAmount;
    }

    function multipleRelease(
        bytes32[] calldata vestingScheduleIds
    ) external nonReentrant whenNotPaused{
        for (uint i; i < vestingScheduleIds.length;) {
            release(vestingScheduleIds[i]);
            unchecked {
                i++;
            }
        }
    }

    /**
     * @notice Release vested amount of tokens.
     * @param vestingScheduleId the vesting schedule identifier
     */
    function release(
        bytes32 vestingScheduleId
    ) public nonReentrant whenNotPaused onlyIfVestingScheduleNotRevoked(vestingScheduleId) {
        VestingSchedule storage vestingSchedule = vestingSchedules[
            vestingScheduleId
        ];
        bool isBeneficiary = msg.sender == vestingSchedule.beneficiary;

        require(
            isBeneficiary,
            "TokenVesting: only beneficiary and owner can release vested tokens"
        );
        uint256 vestedAmount = _computeReleasableAmount(vestingSchedule);
        vestingSchedule.released = vestingSchedule.released + vestedAmount;
        address payable beneficiaryPayable = payable(
            vestingSchedule.beneficiary
        );
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - vestedAmount;
        TransferHelper.safeTransfer(address(rewardToken), beneficiaryPayable, vestedAmount);
    }


    function rewardFromDWStaking(address _beneficiary, uint256 _amount) external override {
        require(msg.sender == DWStaking, "vault: must be called from DWStaking");
        require(rewardApplicable, "vault: ddx reward duration is ended");

        if (block.timestamp > startVestingTime) {
            _createVestingSchedule(_beneficiary, block.timestamp, 0, vestingDuration, vestingPeriodInSeconds, true, _amount);
        } else {
            VestingSchedule memory vestingSchedule = getVestingSchedule(computeVestingScheduleIdForAddressAndIndex(_beneficiary, 0));

            if (vestingSchedule.beneficiary == address(0)) {
                _createVestingSchedule(_beneficiary, startVestingTime, 0, vestingDuration, vestingPeriodInSeconds, true, _amount);
            }

            vestingSchedule.amountTotal += _amount;
        }
    }

    function toggleRewardApplicable() external onlyRole(SUB_ADMIN_ROLE) {
        rewardApplicable = rewardApplicable ? false : true;
    }

    /**
     * @notice Returns the vesting schedule information for a given identifier.
     * @return the vesting schedule structure information
    */
    function getVestingSchedule(
        bytes32 vestingScheduleId
    ) public view returns (VestingSchedule memory) {
        return vestingSchedules[vestingScheduleId];
    }

    /**
     * @notice Returns the vesting schedule information for a given holder and index.
     * @return the vesting schedule structure information
     */
    function getVestingScheduleByAddressAndIndex(
        address holder,
        uint256 index
    ) public view returns (VestingSchedule memory) {
        return
            getVestingSchedule(
                computeVestingScheduleIdForAddressAndIndex(holder, index)
            );
    }

     /**
     * @dev Computes the vesting schedule identifier for an address and an index.
     */
    function computeVestingScheduleIdForAddressAndIndex(
        address holder,
        uint256 index
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(holder, index));
    }

    /**
     * @dev Computes the next vesting schedule identifier for a given holder address.
     */
    function computeNextVestingScheduleIdForHolder(
        address holder
    ) public view returns (bytes32) {
        return
            computeVestingScheduleIdForAddressAndIndex(
                holder,
                holdersVestingCount[holder]
            );
    }

    function _verifyAllowance(address _user, address _token, uint _amount) internal view returns(bool) {
        uint allowance = IERC20(_token).allowance(_user, address(this));
        return allowance >= _amount;
    }


    function _deliverTokens(address _beneficiary, uint _amount) internal {
        TransferHelper.safeTransferFrom(address(rewardToken), treasury, _beneficiary, _amount);
    }

    /**
     * @dev Computes the releasable amount of tokens for a vesting schedule.
     * @return the amount of releasable tokens
     */
    function _computeReleasableAmount(
        VestingSchedule memory vestingSchedule
    ) internal view returns (uint256) {
        // Retrieve the current time.
        uint256 currentTime = block.timestamp;
        // If the current time is before the cliff, no tokens are releasable.
        if ((currentTime < vestingSchedule.cliff) || vestingSchedule.revoked) {
            return 0;
        }
        // If the current time is after the vesting period, all tokens are releasable,
        // minus the amount already released.
        else if (
            currentTime >= vestingSchedule.start + vestingSchedule.duration
        ) {
            return vestingSchedule.amountTotal - vestingSchedule.released;
        }
        // Otherwise, some tokens are releasable.
        else {
            // Compute the number of full vesting periods that have elapsed.
            uint256 timeFromStart = currentTime - vestingSchedule.start;
            uint256 secondsPerSlice = vestingSchedule.slicePeriodSeconds;
            uint256 vestedSlicePeriods = timeFromStart / secondsPerSlice;
            uint256 vestedSeconds = vestedSlicePeriods * secondsPerSlice;
            // Compute the amount of tokens that are vested.
            uint256 vestedAmount = (vestingSchedule.amountTotal *
                vestedSeconds) / vestingSchedule.duration;
            // Subtract the amount already released and return.
            return vestedAmount - vestingSchedule.released;
        }
    }

    /**
     * @notice Creates a new vesting schedule for a beneficiary.
     * @param _beneficiary address of the beneficiary to whom vested tokens are transferred
     * @param _start start time of the vesting period
     * @param _cliff duration in seconds of the cliff in which tokens will begin to vest
     * @param _duration duration in seconds of the period in which the tokens will vest
     * @param _slicePeriodSeconds duration of a slice period for the vesting in seconds
     * @param _revocable whether the vesting is revocable or not
     * @param _amount total amount of tokens to be released at the end of the vesting
     */
    function _createVestingSchedule(
        address _beneficiary,
        uint256 _start,
        uint256 _cliff,
        uint256 _duration,
        uint256 _slicePeriodSeconds,
        bool _revocable,
        uint256 _amount
    ) internal {
        require(_duration > 0, "TokenVesting: duration must be > 0");
        require(_amount > 0, "TokenVesting: amount must be > 0");
        require(
            _slicePeriodSeconds >= 1,
            "TokenVesting: slicePeriodSeconds must be >= 1"
        );
        require(_duration >= _cliff, "TokenVesting: duration must be >= cliff");
        bytes32 vestingScheduleId = computeNextVestingScheduleIdForHolder(
            _beneficiary
        );
        uint256 cliff = _start + _cliff;
        vestingSchedules[vestingScheduleId] = VestingSchedule(
            _beneficiary,
            cliff,
            _start,
            _duration,
            _slicePeriodSeconds,
            _revocable,
            _amount,
            0,
            false
        );
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount + _amount;
        vestingSchedulesIds.push(vestingScheduleId);
        uint256 currentVestingCount = holdersVestingCount[_beneficiary];
        holdersVestingCount[_beneficiary] = currentVestingCount + 1;
    }




    /// --------------------------------
    /// ------- Modifier Function ------
    /// --------------------------------

    /**
     * @dev Reverts if the vesting schedule does not exist or has been revoked.
     */
    modifier onlyIfVestingScheduleNotRevoked(bytes32 vestingScheduleId) {
        require(!vestingSchedules[vestingScheduleId].revoked);
        _;
    }

}

contracts/DWStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IOracleSimple.sol";
import "./interfaces/IDChainStaking.sol";
import "./interfaces/IDDXVault.sol";
import "./interfaces/IDDXStaking.sol";
import "./interfaces/IDWVault.sol";
import "./DChainBase.sol";


contract DWStaking is IDChainStaking, DChainBase {
    struct StakingInfo {
        uint256 totalExpectedInterest;
        uint256 totalStakesInUSD;
        uint256 claimedInterest;
        uint64 claimDuration;
        uint64 dueDate;
        uint64 lastClaimedTime;
        uint64 lastUpdatedTime;
        uint64 createdAt;
    }

    struct StakeToken {
        bool created;
    }

    struct OfferedCurrency {
        uint256 decimal;
        uint256 rate;
    }

    uint64 private constant INTEREST_RATE_PRECISION_POINT = (10 ** 18); 
    uint32 private constant ONE_DAY_IN_SECONDS = 1 days;
    uint32 private constant ONE_YEAR_IN_SECONDS = 365 days;


    IERC20 public rewardToken;
    IERC20 public extraRewardToken;

    IDDXVault public DDXVault;
    IDWVault public vault;
    IDDXStaking public DDXStaking;

    address public treasury;
    address public admin;

    uint256 public totalStakingContracts;

    uint256 public totalStaked;
    uint256 public minimumStakingAmountInUSD; 
    uint256 public maximumEarningsInPercent;

    uint64 public claimDuration;
    uint256 public directInterest;

    uint256[9] public commissionInterestLevels;

    bool public emergencyCancelled;

    // Asset token -> Offered Currency (to $)
    mapping(address => OfferedCurrency) public offeredCurrencies;
    mapping(address => StakeToken) public allowedStakeTokens;
    // Asset token => oracle
    mapping(address => address) public assetPrices;
    // referrer -> number of F1s
    mapping(address => uint256) public totalReferralInvitations;
    // sender -> referrer 
    mapping(address => address) public referredBy;
    // sender -> current staking infos
    mapping(uint => StakingInfo) public stakingContracts;
    // staking contract id -> owner
    mapping(uint => address) public stakingContractOwnedBy;

    event ContractCreated(uint256 indexed contractId, address indexed user, address indexed stakeToken, address referrer, uint256 amount, uint256 amountInUSD);
    event StakingContractCreated(address indexed user, address indexed referrer, uint indexed contractId, uint stakingAmount);
    event Withdraw(address indexed user, uint256 amount);
    event RewardHarvested(address indexed claimer, uint256 amount, uint256 amountInUSD);

    function initialize(address _owner, address _treasury, IERC20 _rewardToken, IERC20 _extraRewardToken) external initializer {
        __DChainBase_init(_owner);

        /// @dev: ZA - Zero address
        require(_treasury != address(0), "ZA");
        require(address(_rewardToken) != address(0), "ZA");

        treasury = _treasury;
        rewardToken = _rewardToken;
        extraRewardToken = _extraRewardToken;
        admin = msg.sender;

        /// Commission for the invitation and only get once
        commissionInterestLevels = [
            1500, // 15%
            1000, // 10%
            500,  // 5%
            500,  // 5%
            500,  // 5%
            0,
            0,
            0,
            0
        ];


        claimDuration = 600 days; // 20 months
        directInterest = 50000000000000000; // 5%
        minimumStakingAmountInUSD = 100 * (10 ** 6); // Minimum will be 100$
        maximumEarningsInPercent = 2000000000000000000; // 200%

        _setupRole(SUB_ADMIN_ROLE, _owner);

        // Max approve for transfer from 
        rewardToken.approve(address(this), type(uint256).max);
    }

    /// -----------------------------------
    /// --------- Update Function ---------
    /// -----------------------------------

    function setDDXStaking(IDDXStaking _staking) external onlyRole(SUB_ADMIN_ROLE) {
        require(address(_staking) != address(0), "pool: DDX Staking cannot be zero address");
        DDXStaking = _staking;
    }

    function setDWVault(IDWVault _vault) external onlyRole(SUB_ADMIN_ROLE) {
        require(address(_vault) != address(0), "pool: DW vault cannot be zero address");
        vault = _vault;
    }

    function setDDXVault(IDDXVault _vault) external onlyRole(SUB_ADMIN_ROLE) {
        require(address(_vault) != address(0), "pool: DDX vault cannot be zero address");
        DDXVault = _vault;
    }

    function setAssetOracle(address _pegToken, address _oracle) external onlyRole(SUB_ADMIN_ROLE) {
        assetPrices[_pegToken] = _oracle;
    }

    // how to convert from 1 Token - to $
    function setOfferedCurrency(address _currency, uint _rate, uint _decimal) external onlyRole(SUB_ADMIN_ROLE) {
        OfferedCurrency storage offeredCurrency = offeredCurrencies[_currency];
        offeredCurrency.rate = _rate;
        offeredCurrency.decimal = _decimal;
    }

    function setAllowedStakeToken(address _stakeToken) external onlyRole(SUB_ADMIN_ROLE) {
        StakeToken storage stakeToken = allowedStakeTokens[_stakeToken];
        require(!stakeToken.created, "Allowed token is already existed!");
        stakeToken.created = true;
    }

    /// -----------------------------------
    /// ---------- Core Function ----------
    /// -----------------------------------

    function depositByVault(uint256 _originAmount, uint _lockedAmount, address _user, address _referrer) external override whenNotPaused nonReentrant {
        require(_msgSender() == address(vault), "pool: Not stake by vault");

        uint totalStakeAmount = _originAmount + _lockedAmount;

        require(totalStakeAmount > 0, "pool: amount cannot be zero");

        // Forward from vault to smart contract 
        _forwardRewardToken(address(vault), _originAmount);
        uint amountOutInUSD = _validateMinimumStakingAmount(address(rewardToken), totalStakeAmount);

        // If user choose to stake with platform token, need to specify which peg token you want to convert to 
        if (_originAmount > 0) {
            rewardToken.burn(_originAmount);
        }

        _joinByReferral(_user, _referrer, totalStakeAmount);
        _createStakingContract(_user, address(rewardToken), _referrer, totalStakeAmount, amountOutInUSD);

        _rewardDDXToUser(amountOutInUSD, _user);
    }

    function deposit(uint256 amount, address stakeToken, address referrer) external whenNotPaused nonReentrant {
        address sender = msg.sender;

        require(amount > 0, "pool: amount cannot be zero");
        require(sender != address(0), "pool: stake address can not be zero address");

        // Forward user tokens to smart contract 
        uint stakingAmount = _forwardRewardToken(sender, amount);
        uint amountOutInUSD = _validateMinimumStakingAmount(stakeToken, stakingAmount);

        // If uesr choose to stake with other tokens, the token needs to be in the allowed list.
        if (stakeToken != address(rewardToken)) {
            StakeToken memory allowedStakeToken = allowedStakeTokens[stakeToken];
            require(allowedStakeToken.created, "pool: stake token is not allowed");
        }

        // If user choose to stake with platform token, need to specify which peg token you want to convert to 
        if (stakeToken == address(rewardToken)) {
            rewardToken.burn(stakingAmount);
        }

        _joinByReferral(sender, referrer, stakingAmount);
        _createStakingContract(sender, stakeToken, referrer, stakingAmount, amountOutInUSD);

        _rewardDDXToUser(amountOutInUSD, sender);
    }

    function claimMultRewards(uint[] memory _contractIds) external nonReentrant whenNotPaused {
        for (uint i; i < _contractIds.length;) {
            claimReward(_contractIds[i]);
            unchecked {
                i++;
            }
        }
    }

    function claimReward(uint _contractId) public nonReentrant whenNotPaused {
        address sender = msg.sender;
        require(stakingContractOwnedBy[_contractId] == sender, "pool: contract id not belongs to this owner");
        _harvest(sender, _contractId);
    }

    function _harvest(address _sender, uint _contractId) internal {
        StakingInfo storage stakingInfo = stakingContracts[_contractId];

        uint256 rewardsInUSD = pendingRewardInUSD(_contractId);
        uint256 rewardsInRewardTokens = _convertUSDToRewardToken(rewardsInUSD);

        require(rewardsInRewardTokens > 0, "pool: reward not enough to harvest");

        // Transfer the interest amount to owner
        rewardToken.transferFrom(treasury, _sender, rewardsInRewardTokens);

        uint64 lastClaimedTime = uint64(block.timestamp);

        if (lastClaimedTime > stakingInfo.dueDate) {
            lastClaimedTime = stakingInfo.dueDate;
        }

        stakingInfo.lastUpdatedTime = uint64(block.timestamp);
        stakingInfo.lastClaimedTime = lastClaimedTime;
        stakingInfo.claimedInterest += rewardsInUSD;

        emit RewardHarvested(_sender, rewardsInRewardTokens, rewardsInUSD);
    }

    function pendingRewardInUSD(uint256 _contractId) public view returns (uint256) {
        StakingInfo memory stakingInfo = stakingContracts[_contractId];

        uint64 current = uint64(block.timestamp);

        if (current <= stakingInfo.lastClaimedTime) {
            return 0;
        }

        if (current > stakingInfo.dueDate) {
            current = stakingInfo.dueDate;
        }

        // Get total number of interest periods from the last time claimed
        uint64 passedDuration = uint64(block.timestamp) - stakingInfo.lastClaimedTime; 

        uint256 pendingInterest = stakingInfo.totalExpectedInterest * passedDuration * INTEREST_RATE_PRECISION_POINT / claimDuration / INTEREST_RATE_PRECISION_POINT;

        return pendingInterest;
    }

    // Reward token - asset
    function _validateMinimumStakingAmount(address _stakeToken, uint256 _stakingAmount) internal returns(uint256 totalStakingAmountInUSD) {
        address oracle = assetPrices[_stakeToken];

        uint256 amountOut;

        if (oracle != address(0)) {
            // Update oracle Pricing when time elapsed has passed
            if (block.timestamp - IOracleSimple(oracle).getBlockTimestampLast() >  IOracleSimple(oracle).PERIOD()) { 
                IOracleSimple(oracle).update();
            }

            amountOut = IOracleSimple(oracle).consult(_stakeToken, _stakingAmount);
        }

        if (amountOut > 0) {
            totalStakingAmountInUSD = amountOut;
        } else { 
            // This case fallback to pre setup price
            totalStakingAmountInUSD = getStakingAmountInUSD(_stakeToken, _stakingAmount);  // Get amount in case we don't have an active oracle
        }

        // Needs to be make sure staking amount by $ greater than minimum amount
        require(totalStakingAmountInUSD >= minimumStakingAmountInUSD, "pool: minimum staking amount not reached");
    }

    function _convertUSDToRewardToken(uint256 _amountInUSD) internal returns(uint256) {
        address oracle = assetPrices[address(rewardToken)];

        if (oracle != address(0)) {
            // Update oracle Pricing when time elapsed has passed
            if (block.timestamp - IOracleSimple(oracle).getBlockTimestampLast() >  IOracleSimple(oracle).PERIOD()) { 
                IOracleSimple(oracle).update();
            }

            return IOracleSimple(oracle).consult(IOracleSimple(oracle).token1(), _amountInUSD);
        }

        // Get amount in case we don't have an active oracle
        return getStakingAmountByPegToken(address(rewardToken), _amountInUSD); 
    }

    function _convertUSDToExtraRewardToken(uint256 _amountInUSD) internal returns(uint256) {
        address oracle = assetPrices[address(extraRewardToken)];

        if (oracle != address(0)) {
            // Update oracle Pricing when time elapsed has passed
            if (block.timestamp - IOracleSimple(oracle).getBlockTimestampLast() >  IOracleSimple(oracle).PERIOD()) { 
                IOracleSimple(oracle).update();
            }

            return IOracleSimple(oracle).consult(IOracleSimple(oracle).token1(), _amountInUSD);
        }

        // Get amount in case we don't have an active oracle
        return getStakingAmountByPegToken(address(extraRewardToken), _amountInUSD); 
    }

    /**
     * @dev Get Staking token amount in offered currencies
     * @param _amount Amount of purchase token
     * @return Return amount of respective IDO token
     */
    function getStakingAmountInUSD(
        address _token,
        uint _amount
    ) public view returns (uint) {
        OfferedCurrency memory currency = offeredCurrencies[_token];
        return
            (_amount * (10 ** currency.decimal)) / currency.rate;
    }

    function getStakingAmountByPegToken(
        address _token,
        uint _amount
    ) public view returns (uint) {
        OfferedCurrency memory currency = offeredCurrencies[_token];
        return
            (_amount * currency.rate) / (10 ** currency.decimal);
    }

    function _forwardRewardToken(address sender, uint256 amount) internal returns(uint256) {
        uint256 totalStakingbefore = rewardToken.balanceOf(address(this));
        // Locking principal deposit amount
        rewardToken.transferFrom(sender, address(this), amount);
        // Get balance of contract after staking
        uint256 totalStakingAfter = rewardToken.balanceOf(address(this));

        return totalStakingAfter - totalStakingbefore;
    }

    function _joinByReferral(address _sender, address _referrer, uint _stakingAmount) internal {
        // Validate Referrals
        if (_referrer != address(0)) {
            require(referredBy[_sender] == address(0) && _referrer != _sender,"pool: user already joined by referral");

            referredBy[_sender] = _referrer;
            totalReferralInvitations[_referrer] += 1;

            // Give direct intetest to the refferer
            uint256 directInterestForReffer = _stakingAmount * directInterest / INTEREST_RATE_PRECISION_POINT;
            rewardToken.transferFrom(treasury, _referrer, directInterestForReffer);
        }
    }

    function _createStakingContract(address _user, address _stakeToken, address _referrer, uint _stakingAmount, uint _amountOutInUSD) internal {
        uint contractId = totalStakingContracts;

        StakingInfo storage stakingInfo = stakingContracts[contractId];
        stakingInfo.createdAt = uint64(block.timestamp);
        stakingInfo.lastClaimedTime = uint64(block.timestamp);
        stakingInfo.lastUpdatedTime = uint64(block.timestamp);
        stakingInfo.totalStakesInUSD = _amountOutInUSD;
        stakingInfo.totalExpectedInterest =  stakingInfo.totalStakesInUSD  * maximumEarningsInPercent / INTEREST_RATE_PRECISION_POINT;
        stakingInfo.claimDuration = claimDuration;
        stakingInfo.dueDate = stakingInfo.createdAt +  stakingInfo.claimDuration;

        totalStaked += _stakingAmount;
        totalStakingContracts++;

        stakingContractOwnedBy[contractId] = _user;

        emit ContractCreated(contractId, _user, _stakeToken, _referrer, _stakingAmount, _amountOutInUSD);
    }

    function _rewardDDXToUser(uint _amountOutInUSD, address _user) internal {
        // Transfer extra DDX reward token to investor
        uint extraRewardTokenAmount = DDXStaking.getAmountDDXByUSD(_amountOutInUSD);
        if (extraRewardTokenAmount > 0 && DDXVault.rewardApplicable()) {
            DDXVault.rewardFromDWStaking(_user, extraRewardTokenAmount);
        }
    }

    /// -----------------------------------
    /// --------- Pause Function ----------
    /// -----------------------------------

    function pause() external onlyRole(SUB_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(SUB_ADMIN_ROLE) {
        _unpause();
    }
}

contracts/DWVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IDChainStaking.sol";
import "./libraries/TransferHelper.sol";
import "./DChainBase.sol";


contract DWVault is DChainBase {
    event PendingSoldTokenClaimed(
        address indexed claimer,
        address indexed beneficiary,
        uint soldTokens
    );

    event BuyTokenByToken(
        address indexed investor,
        address indexed offerToken,
        address indexed beneficiary,
        uint amount,
        uint soldTokens
    );

     event TokenReleased(
        address indexed beneficiary,
        uint vestedAmount
    );

    struct VestingSchedule {
        // beneficiary of tokens after they are released
        address beneficiary;
        // cliff time of the vesting start in seconds since the UNIX epoch
        uint256 cliff;
        // start time of the vesting period in seconds since the UNIX epoch
        uint256 start;
        // duration of the vesting period in seconds
        uint256 duration;
        // duration of a slice period for the vesting in seconds
        uint256 slicePeriodSeconds;
        // whether or not the vesting is revocable
        bool revocable;
        // total amount of tokens to be released at the end of the vesting
        uint256 amountTotal;
        // amount of tokens released
        uint256 released;
        // whether or not the vesting has been revoked
        bool revoked;
    }

    struct OfferedCurrency {
        uint256 decimals;
        uint256 rate;
        bool created;
    }

    uint256 private constant PRECISION_POINT = 10000;

    uint256 public tgePercentage;

    uint public openTime;
    uint public closeTime;
    uint public lockBeforeVesting;
    uint public vestingDuration;
    uint public vestingPeriodInSeconds;

    address public treasury;
    address public admin;
    address public fundReceiver;
    address public DWStaking;

    IERC20 public soldToken;

    uint public totalRaiseAmount; // In Sold token
    uint public totalRaised; // in Offer currency

    uint public totalSold; // In Sold token

    uint256 private vestingSchedulesTotalAmount;

    // Asset token -> Offered Currency (to $)
    mapping(address => OfferedCurrency) public offeredCurrencies;
    // Sender -> pending claim
    mapping(address => uint) public pendingSoldTokenClaim;
    // sender -> vesting schedule
    mapping(address => VestingSchedule) public vestingSchedules;
    function initialize(
        address _owner,
        IERC20 _soldToken, 
        address _treasury, 
        address _fundReceiver, 
        address _DWStaking,
        uint _tgePercentage,
        uint _totalRaiseAmount,
        uint _lockBeforeVesting,
        uint _openTime,
        uint _duration
    ) external initializer {
        __DChainBase_init(_owner);

        /// @dev: ZA - Zero address
        require(_DWStaking != address(0), "ZA");
        require(address(_soldToken) != address(0), "ZA");
        require(_treasury != address(0), "ZA");
        require(_fundReceiver != address(0), "ZA");
        require(_tgePercentage <= PRECISION_POINT, "EOP");

        openTime = _openTime;
        closeTime = openTime + _duration;
        lockBeforeVesting = _lockBeforeVesting;

        soldToken = _soldToken;
        treasury = _treasury;
        fundReceiver = _fundReceiver;
        DWStaking = _DWStaking;
        totalRaiseAmount = _totalRaiseAmount;
        tgePercentage = _tgePercentage;
        admin = msg.sender;

        vestingDuration = 20 * 30 days; // 5% per month
        vestingPeriodInSeconds = 30 days; // one per month
    }

    /**
     * @notice Release vested amount of tokens.
     */
    function release() public nonReentrant {
        VestingSchedule storage vestingSchedule = vestingSchedules[
            msg.sender
        ];
        bool isBeneficiary = msg.sender == vestingSchedule.beneficiary;

        require(
            isBeneficiary,
            "TokenVesting: only beneficiary can release vested tokens"
        );
        uint256 vestedAmount = _computeReleasableAmount(vestingSchedule);
        require(
            vestedAmount > 0,
            "TokenVesting: cannot release tokens, not enough vested tokens"
        );
        vestingSchedule.released = vestingSchedule.released + vestedAmount;
        address payable beneficiaryPayable = payable(
            vestingSchedule.beneficiary
        );
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - vestedAmount;
        _deliverTokens(beneficiaryPayable, vestedAmount);

        emit TokenReleased(beneficiaryPayable, vestedAmount);
    }

    function stakeWithVault(
        uint256 _originAmount,
        uint256 _lockedAmount,
        address _referrer
    ) external {
        require(availableToStakeThroughVault(), "pool: not allow to stake after vesting start");

        address sender = msg.sender;
        VestingSchedule storage vestingSchedule = vestingSchedules[sender];

        require(vestingSchedule.beneficiary == sender, "pool: vesting schedule not existed");
        require(soldToken.allowance(sender, address(this)) >= _originAmount, "pool: allowance not enough");
        require(vestingSchedule.amountTotal >= _lockedAmount, "pool: vesting schedule amount total not enough to cover staking");

        if (_originAmount > 0) {
            TransferHelper.safeTransferFrom(address(soldToken), _msgSender(), address(this), _originAmount);
            soldToken.approve(DWStaking, _originAmount);
        }

        IDChainStaking(DWStaking).depositByVault(_originAmount, _lockedAmount, sender, _referrer);

        vestingSchedule.amountTotal -= _lockedAmount;
    }

    function buyTokenWithToken(address _offerToken, address _beneficiary, uint256 _amount) external whenNotPaused nonReentrant {
        OfferedCurrency memory offeredCurrency = offeredCurrencies[_offerToken];

        require(offeredCurrency.rate != 0, "PresalePool::Offer currency rate is invalid!");
        require(_validPurchase(), "PresalePool::Pool is ended!");
        require(_verifyAllowance(msg.sender, _offerToken, _amount), "PresalePool::Allowance for offered token unreached!");

        uint soldTokenAmount = _getSoldTokensByOfferedCurrency(_offerToken, _amount);
        require(soldTokenAmount + totalSold <= totalRaiseAmount, "PresalePool::Purchase amount exceeds max amount!");

        _forwardFundTransfer(_offerToken, _amount);

        pendingSoldTokenClaim[_beneficiary] += soldTokenAmount;

        totalRaised = totalRaised + _amount;
        totalSold = totalSold + soldTokenAmount;

        emit BuyTokenByToken(_msgSender(), _offerToken, _beneficiary, _amount, soldTokenAmount);
    }

    function claim(address _beneficiary) external whenNotPaused nonReentrant {
        uint pendingTokenAmount = pendingSoldTokenClaim[_msgSender()];

        require(block.timestamp > closeTime, "pool: sale is not ended");
        require(block.timestamp <= closeTime + lockBeforeVesting, "pool: claim time is ended");
        require(pendingTokenAmount > 0, "pool: pending claim amount must be positive");

        // Calculate TGE Amount
        uint tgeAmount = pendingTokenAmount * tgePercentage / PRECISION_POINT;
        uint remainmingAmount = pendingTokenAmount - tgeAmount;

        // Deliver TGE Amount to user's beneficiary address
        if (tgeAmount > 0) {
            _deliverTokens(_beneficiary, tgeAmount);
        }

        VestingSchedule storage vestingSchedule = vestingSchedules[_beneficiary];

        if (vestingSchedule.beneficiary == address(0)) {
            _createVestingSchedule(_beneficiary, closeTime + lockBeforeVesting, 0, vestingDuration, vestingPeriodInSeconds, true, remainmingAmount);
        } else {
            vestingSchedule.amountTotal += remainmingAmount;
        }

        pendingSoldTokenClaim[_msgSender()] = 0;

        emit PendingSoldTokenClaimed(
            _msgSender(),
            _beneficiary,
            pendingTokenAmount   
        );
    }

    function setTGEPercentage(uint256 _tgePercentage) external onlyAdmin {
        require(_tgePercentage <= PRECISION_POINT, "EOP");
        tgePercentage = _tgePercentage;
    }

    function setTotalRaiseAmount(uint256 _totalRaiseAmount) external onlyAdmin {
        totalRaiseAmount = _totalRaiseAmount;
    }

    // how to convert from 1 Token - to $
    function setOfferedCurrency(address _currency, uint _rate, uint _decimals) external onlyAdmin {
        OfferedCurrency storage offeredCurrency = offeredCurrencies[_currency];
        offeredCurrency.rate = _rate;
        offeredCurrency.decimals = _decimals;
    }

    function releasableAmount(address _user) external view returns(uint) {
        return _computeReleasableAmount(vestingSchedules[_user]);
    }

    function availableToStakeThroughVault() public view returns(bool) {
        return block.timestamp <= closeTime + lockBeforeVesting;
    }

      function _verifyAllowance(address _user, address _token, uint _amount) internal view returns(bool) {
        uint allowance = IERC20(_token).allowance(_user, address(this));
        return allowance >= _amount;
    }

    function _getSoldTokensByOfferedCurrency(address _token, uint _amount) internal view returns(uint) {
        OfferedCurrency memory offeredCurrency = offeredCurrencies[_token];
        return _amount * offeredCurrency.rate / (10 ** offeredCurrency.decimals);      
    }

    function _deliverTokens(address _beneficiary, uint _amount) internal {
        TransferHelper.safeTransferFrom(address(soldToken), treasury, _beneficiary, _amount);
    }

    function _forwardFundTransfer(address _token, uint _value) internal {
        TransferHelper.safeTransferFrom(_token, _msgSender(), fundReceiver, _value);
    }

    function _validPurchase() internal view returns(bool) {
        bool withinPerioid = block.timestamp >= openTime && block.timestamp <= closeTime;
        return withinPerioid;
    }

    /**
     * @dev Computes the releasable amount of tokens for a vesting schedule.
     * @return the amount of releasable tokens
     */
    function _computeReleasableAmount(
        VestingSchedule memory vestingSchedule
    ) internal view returns (uint256) {
        // Retrieve the current time.
        uint256 currentTime = block.timestamp;
        // If the current time is before the cliff, no tokens are releasable.
        if ((currentTime < vestingSchedule.cliff) || vestingSchedule.revoked) {
            return 0;
        }
        // If the current time is after the vesting period, all tokens are releasable,
        // minus the amount already released.
        else if (
            currentTime >= vestingSchedule.start + vestingSchedule.duration
        ) {
            return vestingSchedule.amountTotal - vestingSchedule.released;
        }
        // Otherwise, some tokens are releasable.
        else {
            // Compute the number of full vesting periods that have elapsed.
            uint256 timeFromStart = currentTime - vestingSchedule.start;
            uint256 secondsPerSlice = vestingSchedule.slicePeriodSeconds;
            uint256 vestedSlicePeriods = timeFromStart / secondsPerSlice;
            uint256 vestedSeconds = vestedSlicePeriods * secondsPerSlice;
            // Compute the amount of tokens that are vested.
            uint256 vestedAmount = (vestingSchedule.amountTotal *
                vestedSeconds) / vestingSchedule.duration;
            // Subtract the amount already released and return.
            return vestedAmount - vestingSchedule.released;
        }
    }

    /**
     * @notice Creates a new vesting schedule for a beneficiary.
     * @param _beneficiary address of the beneficiary to whom vested tokens are transferred
     * @param _start start time of the vesting period
     * @param _cliff duration in seconds of the cliff in which tokens will begin to vest
     * @param _duration duration in seconds of the period in which the tokens will vest
     * @param _slicePeriodSeconds duration of a slice period for the vesting in seconds
     * @param _revocable whether the vesting is revocable or not
     * @param _amount total amount of tokens to be released at the end of the vesting
     */
    function _createVestingSchedule(
        address _beneficiary,
        uint256 _start,
        uint256 _cliff,
        uint256 _duration,
        uint256 _slicePeriodSeconds,
        bool _revocable,
        uint256 _amount
    ) internal {
        require(_duration > 0, "TokenVesting: duration must be > 0");
        require(_amount > 0, "TokenVesting: amount must be > 0");
        require(
            _slicePeriodSeconds >= 1,
            "TokenVesting: slicePeriodSeconds must be >= 1"
        );
        require(_duration >= _cliff, "TokenVesting: duration must be >= cliff");
        uint256 cliff = _start + _cliff;
        vestingSchedules[_beneficiary] = VestingSchedule(
            _beneficiary,
            cliff,
            _start,
            _duration,
            _slicePeriodSeconds,
            _revocable,
            _amount,
            0,
            false
        );

        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount + _amount;
    }

    /// --------------------------------
    /// ------- Modifier Function ------
    /// --------------------------------
}

contracts/interfaces/IDChainStaking.sol.content:
// SPDX-License-Identifier: MIT

